{"changed":true,"filter":false,"title":"WPXMLRPClient.php","tooltip":"/WPXMLRPClient.php","value":"class WPXMLRPClient {\n    var $XMLRPCURL = \"\";\n    var $UserName  = \"\";\n    var $PassWord = \"\";\n    \n    // Constructor\n    public function __construct($xmlrpcurl, $username, $password) \n    {\n        $this->XMLRPCURL = $xmlrpcurl;\n        $this->UserName  = $username;\n        $this->PassWord = $password;\n    }\n    \n    function send_request($requestname, $params)\n    {\n        $request = xmlrpc_encode_request($requestname, $params);\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_POSTFIELDS, $request);\n        curl_setopt($ch, CURLOPT_URL, $this->XMLRPCURL);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n        curl_setopt($ch, CURLOPT_TIMEOUT, 1);\n        $results = curl_exec($ch);\n        curl_close($ch);\n        return $results;\n    }\n    \n    function create_post($title,$body,$category,$keywords='',$encoding='UTF-8')\n    {\n        $title = htmlentities($title,ENT_NOQUOTES,$encoding);\n        $keywords = htmlentities($keywords,ENT_NOQUOTES,$encoding);\n     \n        $content = array(\n            'title' => $title,\n            'description' => $body,\n            'mt_allow_comments' => 0,  // 1 to allow comments\n            'mt_allow_pings' => 0,  // 1 to allow trackbacks\n            'post_type' => 'post',\n            'mt_keywords' => $keywords,\n            'categories' => array($category)\n        );\n        $params = array(0,$this->UserName,$this->PassWord,$content,true);\n     \n        return $this->send_request('metaWeblog.newPost',$params);\n    }\n}","undoManager":{"mark":-1,"position":27,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":26}},"text":"class XMLRPClientWordPress"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":0,"column":26},"end":{"row":0,"column":27}},"text":" "}]}],[{"group":"doc","d